$(function(){$("input[type=file]").each(function(b,d){if(typeof $(this).attr("data-bfi-disabled")!="undefined"){return}var c="Browse";if(typeof $(this).attr("title")!="undefined"){c=$(this).attr("title")}var a=$("<div>").append($(d).eq(0).clone()).html();$(d).replaceWith('<a class="clearfix file-input-wrapper"><span class="file-input"></span><span class="file-input-title btn">'+c+"</span>"+a+"</a>")}).promise().done(function(){$(".file-input-wrapper").mousemove(function(i){var f,a,h,g,e,b,d,c;a=$(this);f=a.find("input");h=a.offset().left;g=a.offset().top;e=f.width();b=f.height();d=i.pageX;c=i.pageY;moveInputX=d-h-e+20;moveInputY=c-g-(b/2);f.css({left:moveInputX,top:moveInputY})});$(".file-input-wrapper input[type=file]").change(function(){$(this).next(".file-input-name").remove();if($(this).prop("files").length>1){$(this).parent().append('<span class="file-input-name">'+$(this)[0].files.length+" files</span>")}else{$(this).parent().append('<span class="file-input-name">'+$(this).val().replace("C:\\fakepath\\","")+"</span>")}})})});